create table ad
(
    ad_id       int(11) unsigned auto_increment comment '广告id'
        primary key,
    pid         int(11) unsigned   default 0  not null comment '广告位置ID',
    media_type  tinyint unsigned   default 0  not null comment '广告类型',
    ad_name     varchar(60)        default '' not null comment '广告名称',
    ad_link     varchar(255)       default '' not null comment '链接地址',
    ad_code     text                          not null comment '图片地址',
    start_time  int                default 0  not null comment '投放时间',
    end_time    int                default 0  not null comment '结束时间',
    link_man    varchar(60)        default '' not null comment '添加人',
    link_email  varchar(60)        default '' not null comment '添加人邮箱',
    link_phone  varchar(60)        default '' not null comment '添加人联系电话',
    click_count mediumint unsigned default 0  not null comment '点击量',
    enabled     tinyint unsigned   default 1  not null comment '是否显示',
    orderby     smallint           default 50 null comment '排序',
    target      tinyint(1)         default 0  null comment '是否开启浏览器新窗口',
    bgcolor     varchar(20)                   null comment '背景颜色',
    ad_key      varchar(255)       default '' null comment 'APP端广告位key,该值固定, APP端通过该key索引对应广告位'
)
    charset = utf8;

create index enabled
    on ad (enabled);

create index position_id
    on ad (pid);

