create table goods
(
    goods_id           int(11) unsigned auto_increment comment '商品id'
        primary key,
    cat_id1            int(11) unsigned        default 0                     not null comment '一级分类id',
    cat_id2            int                     default 0                     null comment '二级分类',
    cat_id3            int                     default 0                     null comment '三级分类',
    store_cat_id1      int                     default 0                     null comment '本店一级分类',
    store_cat_id2      int                     default 0                     null comment '本店二级分类',
    goods_sn           varchar(60)             default ''                    not null comment '商品编号',
    goods_name         varchar(120)            default ''                    not null comment '商品名称',
    click_count        int unsigned            default 0                     not null comment '点击数',
    brand_id           smallint unsigned       default 0                     not null comment '品牌id',
    store_count        int(5)                  default 10                    not null comment '库存数量',
    collect_sum        int(10)                 default 0                     null comment '商品收藏数',
    comment_count      smallint(5)             default 0                     null comment '商品评论数',
    weight             int(11) unsigned        default 0                     not null comment '商品重量克为单位',
    volume             double(10, 4) unsigned  default 0.0000                not null comment '商品体积。单位立方米',
    market_price       decimal(10, 2) unsigned default 0.00                  not null comment '市场价',
    shop_price         decimal(10, 2) unsigned default 0.00                  not null comment '本店价',
    cost_price         decimal(10, 2)          default 0.00                  null comment '商品成本价',
    exchange_integral  int(10)                 default 0                     not null comment 'COMMENT ''积分兑换：0不参与积分兑换',
    keywords           varchar(255)            default ''                    not null comment '商品关键词',
    goods_remark       varchar(420)            default ''                    not null comment '商品简单描述',
    goods_content      text                                                  not null comment '商品详细描述',
    mobile_content     text                                                  null comment '手机端商品详情',
    original_img       varchar(255)            default ''                    not null comment '商品上传原始图',
    is_virtual         tinyint(1) unsigned     default 0                     not null comment '是否是虚拟商品 1是 0否',
    virtual_indate     int                     default 0                     null comment '虚拟商品有效期',
    virtual_limit      smallint                default 0                     null comment '虚拟商品购买上限',
    virtual_refund     tinyint(1)              default 1                     null comment '是否允许过期退款， 1是，0否',
    is_on_sale         tinyint(1) unsigned     default 1                     not null comment '0下架1上架2违规下架',
    is_free_shipping   tinyint(1) unsigned     default 0                     not null comment '是否包邮0否1是',
    on_time            int unsigned            default 0                     not null comment '商品上架时间',
    sort               smallint(4) unsigned    default 50                    not null comment '商品排序',
    is_recommend       tinyint(1) unsigned     default 0                     not null comment '是否推荐',
    is_new             tinyint(1) unsigned     default 0                     not null comment '是否新品',
    is_hot             tinyint(1)              default 0                     null comment '是否热卖',
    goods_type         int(11) unsigned        default 0                     not null comment '商品所属类型id，取值表goods_type的id',
    give_integral      int                     default 0                     null comment '购买商品赠送积分',
    sales_sum          int                     default 0                     null comment '商品销量',
    virtual_sales_sum  int(11) unsigned        default 0                     not null comment '虚拟销售量',
    prom_type          tinyint(1)              default 0                     null comment '0默认1抢购2团购3优惠促销4预售5虚拟(5其实没用)6拼团',
    prom_id            int                     default 0                     null comment '优惠活动id',
    distribut          decimal(10, 2)          default 0.00                  null comment '佣金用于分销分成',
    store_id           int                     default 0                     null comment '商家店铺ID（初始）',
    spu                varchar(128)            default ''                    null comment 'SPU',
    sku                varchar(128)            default ''                    null comment 'SKU',
    goods_state        tinyint(1)              default 1                     null comment '0待审核1审核通过2审核失败',
    close_reason       varchar(255)                                          null comment '违规下架原因',
    suppliers_id       smallint unsigned       default 0                     null comment '供应商ID',
    template_id        int(11) unsigned        default 0                     null comment '运费模板ID',
    is_own_shop        tinyint(1)              default 0                     null comment '1:第三方自营店,2:总平台自营店',
    video              varchar(255)            default ''                    null comment '视频地址',
    label_id           varchar(255)                                          null comment '商品标签',
    plate_top          int(10)                 default 0                     null comment '关联版式头部',
    plate_bottom       int(10)                 default 0                     null comment '关联版式底部',
    user_id            int(10)                                               null comment '首次发售的持有者',
    last_update        timestamp               default '2020-06-10 00:00:00' not null on update CURRENT_TIMESTAMP comment '最后更新时间',
    goods_certificate  varchar(255)                                          null comment '证书地址',
    dc_id              bigint                  default 0                     null comment '存证ID',
    subscription_time  datetime                                              null comment '认购时间',
    subscription_price decimal(10, 2)          default 0.00                  null comment '认购价格',
    owner_id           int(10)                                               null comment '商品拥有者的id',
    delete_flag        tinyint(1)              default 0                     null comment '删除标记 0：正常   1：删除'
)
    charset = utf8;

create index cat_id
    on goods (cat_id1);

create index exchange_integral
    on goods (exchange_integral);

create index goods_name
    on goods (goods_name);

create index goods_sn
    on goods (goods_sn);

create index store_id
    on goods (store_id);

