create table goods_category
(
    id              smallint unsigned auto_increment comment '商品分类id'
        primary key,
    name            varchar(90)                   default '' not null comment '商品分类名称',
    mobile_name     varchar(64) collate utf8_bin  default '' null comment '手机端显示的商品分类名',
    parent_id       smallint unsigned             default 0  not null comment '父id',
    parent_id_path  varchar(128) collate utf8_bin default '' null comment '家族图谱',
    level           tinyint(1)                    default 0  null comment '等级',
    sort_order      tinyint(1) unsigned           default 50 not null comment '顺序排序',
    is_show         tinyint(1) unsigned           default 1  not null comment '是否显示',
    image           varchar(512) collate utf8_bin default '' null comment '分类图片',
    is_hot          tinyint(1)                    default 0  null comment '是否推荐为热门分类',
    cat_group       tinyint(1)                    default 0  null comment '分类分组默认0',
    commission      tinyint(2)                    default 0  null comment '佣金比例',
    commission_rate tinyint(1)                    default 0  null comment '分佣比例用于分销',
    type_id         int                           default 0  null comment '对应的类型id(商品模型ID)'
)
    charset = utf8;

create index parent_id
    on goods_category (parent_id);

