create table spec_goods_price
(
    item_id     bigint unsigned auto_increment comment '规格商品id'
        primary key,
    goods_id    int                           default 0    not null comment '商品id',
    goods_mark  varchar(255)                  default ''   not null comment '商品规格备注',
    `key`       varchar(255) collate utf8_bin default ''   not null comment '规格键名',
    key_name    varchar(255) collate utf8_bin default ''   not null comment '规格键名中文',
    price       decimal(10, 2)                default 0.00 not null comment '价格',
    store_count int                           default 10   not null comment '库存数量',
    bar_code    varchar(32) collate utf8_bin  default ''   not null comment '商品条形码',
    sku         varchar(128) collate utf8_bin default ''   not null comment 'SKU',
    store_id    int                           default 0    not null comment '店铺商家id',
    spec_img    varchar(255)                  default ''   not null comment '规格商品主图',
    prom_id     int(10)                       default 0    not null comment '参加活动id',
    prom_type   tinyint(2)                    default 0    not null comment '参加活动类型',
    cost        decimal(10, 2)                             null comment '成本价'
)
    charset = utf8;

create index gk
    on spec_goods_price (goods_id, `key`);

create index goods_id
    on spec_goods_price (goods_id);

create index `key`
    on spec_goods_price (`key`);

create index store_id
    on spec_goods_price (store_id);

