create table spec_image
(
    goods_id      int                           default 0  null comment '商品规格图片表id',
    spec_image_id int                           default 0  null comment 'tp_spec_item引用规格项id',
    src           varchar(512) collate utf8_bin default '' null comment '商品规格图片路径',
    store_id      int                           default 0  null comment '商家id'
)
    charset = utf8;

create index goods_id
    on spec_image (goods_id);

create index spec_img_id
    on spec_image (spec_image_id);

create index store_id
    on spec_image (store_id);

